SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pat_registrar_aceite_ficha ( cd_estabelecimento_p bigint, nr_sequencia_p bigint, ie_aceite_p text, nm_usuario_p text) AS $body$
BEGIN


update	pat_ficha_aceite
set	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	ie_aceite	= ie_aceite_p,
	dt_liberacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_p;



if (ie_aceite_p	= 'A') then

	/*Aviso sobre aprovação da ficha de aceite.*/

	CALL pat_enviar_comunic_ficha_aprov(	cd_estabelecimento_p,
					nr_sequencia_p,
					null,
					nm_usuario_p,
					'FA',
					nm_usuario_p);

elsif (ie_aceite_p	= 'R') then

	/*Aviso sobre reprovação da ficha de aceite.*/

	CALL pat_enviar_comunic_ficha_aprov(	cd_estabelecimento_p,
					nr_sequencia_p,
					null,
					nm_usuario_p,
					'FR',
					nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
