CREATE OR REPLACE PROCEDURE pat_vincular_nf_doc_venda ( nr_seq_doc_transf_p bigint, nr_seq_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w				bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	pat_doc_transf_nf
where	nr_seq_nf		= nr_seq_nf_p
and	nr_seq_doc_transf <> nr_seq_doc_transf_p;

if (qt_registro_w > 0) then
	/*'Esta nota fiscal já está vinculada a outro documento de venda!#@#@');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266507);
end if;

insert into pat_doc_transf_nf(
	nr_sequencia,
	nr_seq_doc_transf,
	nr_seq_nf,
	nm_usuario,
	dt_atualizacao,
	nm_usuario_nrec,
	dt_atualizacao_nrec)
values (	nextval('pat_doc_transf_nf_seq'),
	nr_seq_doc_transf_p,
	nr_seq_nf_p,
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp());

commit;

end;
