CREATE OR REPLACE PROCEDURE pa_altera_status_patologia ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_tipo_p text, ie_commit_p text default 'S', nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


nr_seq_status_pato_w	varchar(10);

BEGIN

select 	max(nr_sequencia)
into STRICT	nr_seq_status_pato_w
from	PROCED_PATOLOGIA_STATUS
where	IE_STATUS_PATOLOGIA = ie_tipo_p;

--Bruno 05/05/2010
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') then
	if (nr_seq_status_pato_w IS NOT NULL AND nr_seq_status_pato_w::text <> '') then
		update 	prescr_procedimento
		set	nr_seq_status_pato = nr_seq_status_pato_w
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_seq_prescricao_p;
	end if;
end if;

if ie_commit_p = 'S' then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
