CREATE OR REPLACE PROCEDURE pa_inserir_questoes_laudo (nr_seq_laudo_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_grupo_w	bigint;
nr_seq_peca_w	bigint;

C01 CURSOR FOR
	SELECT   nr_sequencia
	from     grupo_questao_laudo a
	where    not exists (SELECT   1
				from     laudo_grupo_questao x
				where    x.nr_seq_grupo_questao  = a.nr_sequencia
				and      x.nr_seq_Laudo          = nr_seq_laudo_p
				and      coalesce(x.nr_seq_peca::text, '') = '');

C02 CURSOR FOR
	SELECT 	nr_sequencia
	from  	prescr_proc_peca c
	where 	nr_prescricao  	= nr_prescricao_p
	and	nr_seq_prescr 	= nr_seq_prescr_p
	and   	coalesce(nr_seq_peca::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_grupo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		open C02;
		loop
		fetch C02 into
			nr_seq_peca_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			CALL Inserir_laudo_grupo_questao(nr_seq_grupo_w,nr_seq_laudo_p,nm_usuario_p,nr_seq_peca_w);
			end;
		end loop;
		close C02;
	end;
end loop;
close C01;

commit;

end;
