SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pa_liberar_itens_grupo (nr_seq_peca_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_seq_prescr_w		bigint;


BEGIN

update	laudo_questao_item
set	dt_liberacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_seq_laudo_grupo in (SELECT 	nr_sequencia
			       from	laudo_grupo_questao
			       where	nr_seq_peca = nr_seq_peca_p)
and	coalesce(dt_liberacao::text, '') = '';

if (nr_seq_peca_p IS NOT NULL AND nr_seq_peca_p::text <> '') then

	select	max(nr_prescricao),
		max(nr_seq_prescr)
	into STRICT	nr_prescricao_w,
		nr_seq_prescr_w
	from	prescr_proc_peca
	where	nr_sequencia = nr_seq_peca_p;

	if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') and (nr_seq_prescr_w IS NOT NULL AND nr_seq_prescr_w::text <> '') then

		CALL pa_altera_status_patologia( nr_prescricao_w, nr_seq_prescr_w, 'LP', 'N', nm_usuario_p);

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
