CREATE OR REPLACE PROCEDURE pb_alterar_status_tratamento ( nr_seq_tratamento_p bigint, ie_status_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pac_reab_w			bigint;


BEGIN
	update	rp_tratamento
	set	nr_seq_status = ie_status_p
	where	nr_sequencia = nr_seq_tratamento_p;

	select	nr_seq_pac_reav
	into STRICT 	nr_seq_pac_reab_w
	from	rp_tratamento
	where	nr_sequencia = nr_seq_tratamento_p;

	insert 	into rp_log_status(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_pac_reab,
			nr_seq_status_atual)
	values (nextval('rp_log_status_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_pac_reab_w,
			ie_status_p);

commit;

end;
$body$
