SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pb_inativar_equipamento (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

select max(c.nr_sequencia)
into STRICT	nr_sequencia_w
from RP_EQUIPAMENTO a,
	 man_equipamento b,
	 MAN_TIPO_EQUIPAMENTO c
where 1 = 1
and	  a.NR_SEQ_EQUIPAMENTO	= b.NR_SEQUENCIA
and	  b.NR_SEQ_TIPO_EQUIP	= c.NR_SEQUENCIA
and	  a.nr_sequencia = nr_sequencia_p;

update	MAN_TIPO_EQUIPAMENTO
set		IE_SITUACAO = 'I',
		NM_USUARIO =  nm_usuario_p
where 	nr_sequencia = nr_sequencia_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
