CREATE OR REPLACE PROCEDURE pcs_atualizar_inf_so_lista (nr_seq_registro_p bigint, cd_material_p bigint, qt_compra_p bigint, cd_local_est_p bigint, ie_tipo_compra_p text, nr_seq_motivo_solic_p bigint, nm_usuario_p text) AS $body$
BEGIN
