set		vl_informacao = ie_tipo_compra_p
where	nr_seq_registro = nr_seq_registro_p
and		cd_material = cd_material_p
and		nm_informacao = 'IE_TIPO_COMPRA';

update	pcs_reg_analise_itens
set		vl_informacao = nr_seq_motivo_solic_p
where	nr_seq_registro = nr_seq_registro_p
and		cd_material = cd_material_p
and		nm_informacao = 'NR_SEQ_MOTIVO_SOLIC';

