CREATE OR REPLACE PROCEDURE pcs_atualizar_status_parecer ( nr_seq_parecer_p bigint, ds_resposta_p text, ie_acao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN
 
if (coalesce(nr_seq_parecer_p,0) > 0) then 
	update	pcs_parecer 
	set		ie_status = ie_acao_p, 
			dt_resposta = clock_timestamp(), 
			nm_usuario_resp = nm_usuario_p, 
			ds_resposta = ds_resposta_p 
	where	nr_sequencia = nr_seq_parecer_p;
end if;	
 
pcs_enviar_parecer_solicitante(nr_seq_parecer_p,cd_estabelecimento_p,nm_usuario_p);
 
commit;
 
end;
$body$
