CREATE OR REPLACE PROCEDURE pcs_atualiza_dt_entrega ( nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

cd_material_w			integer;
qt_lead_time_w			double precision;
nr_item_solic_compra_w	integer;

C01 CURSOR FOR
	SELECT	distinct b.cd_material,
			c.nr_item_solic_compra,
			(SELECT (vl_informacao)::numeric  from pcs_reg_analise_itens x where x.cd_material = b.cd_material and x.nr_seq_registro = b.nr_seq_registro and x.nm_informacao = 'QT_LEAD_TIME')
	from	pcs_reg_analise_itens b,
			solic_compra_item c
	where	b.nr_solic_compra = c.nr_solic_compra
	and		b.cd_material = c.cd_material
	and		b.nr_solic_compra = nr_solic_compra_p;


BEGIN
open C01;
loop
fetch C01 into
	cd_material_w,
	nr_item_solic_compra_w,
	qt_lead_time_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	solic_compra_item
	set		dt_solic_item = clock_timestamp() + qt_lead_time_w
	where	nr_solic_compra = nr_solic_compra_p
	and		nr_item_solic_compra = nr_item_solic_compra_w;

	update	solic_compra_item_entrega
	set		dt_entrega_solicitada =  clock_timestamp() + qt_lead_time_w
	where	nr_solic_compra = nr_solic_compra_p
	and		nr_item_solic_compra = nr_item_solic_compra_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
