CREATE OR REPLACE PROCEDURE pcs_copiar_locais_grupamento ( nr_seq_grup_origem_p bigint, nr_seq_grup_destino_p bigint, ie_substituir_regras_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_substituir_regras_p = 'S') then
	delete from pcs_local_estoque_grup
	where nr_seq_grupamento = nr_seq_grup_destino_p;
end if;

insert into pcs_local_estoque_grup(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_grupamento,
	cd_estab_regra,
	cd_local_estoque,
	ds_grupamento)
SELECT	nextval('pcs_local_estoque_grup_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_grup_destino_p,
	cd_estab_regra,
	cd_local_estoque,
	pcs_obter_dados_grupamento(nr_seq_grup_destino_p,'DG')
from	pcs_local_estoque_grup
where	nr_seq_grupamento = nr_seq_grup_origem_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
