CREATE OR REPLACE PROCEDURE pcs_copiar_politica_grupamento ( nr_seq_grup_origem_p bigint, nr_seq_grup_destino_p bigint, ie_substituir_regras_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_substituir_regras_p = 'S') then
	delete from pcs_politicas_grupamento
	where nr_seq_grupamento = nr_seq_grup_destino_p;
end if;

insert into pcs_politicas_grupamento(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_grupamento,
	nr_seq_regional,
	cd_estab_regra,
	cd_responsavel,
	qt_politica,
	qt_frequencia,
	qt_margem_dias,
	qt_dias_corte_mlc,
	qt_dias_corte_lc,
	ie_situacao,
	ds_grupamento)
SELECT	nextval('pcs_politicas_grupamento_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_grup_destino_p,
	nr_seq_regional,
	cd_estab_regra,
	cd_responsavel,
	qt_politica,
	qt_frequencia,
	qt_margem_dias,
	qt_dias_corte_mlc,
	qt_dias_corte_lc,
	ie_situacao,
	ds_grupamento
from	pcs_politicas_grupamento
where	nr_seq_grupamento = nr_seq_grup_origem_p
and	ie_situacao = 'A';

commit;

end;
$body$
