CREATE OR REPLACE PROCEDURE pcs_inativar_regra_atividade ( nr_seq_regra_ativ_p bigint, nr_seq_motivo_inativo_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_agenda_w		pcs_agenda_atividades.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pcs_agenda_atividades
	where	nr_seq_regra = nr_seq_regra_ativ_p
	and	coalesce(nr_ordem_servico::text, '') = '';


BEGIN

update	pcs_regra_atividades
set	ie_situacao = 'I',
	nr_seq_motivo_inativo = nr_seq_motivo_inativo_p,
	nm_usuario = nm_usuario_p
where	nr_sequencia = nr_seq_regra_ativ_p;

open C01;
loop
fetch C01 into
	nr_seq_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pcs_agenda_atividades
	set	ie_situacao = 'I',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		nr_seq_motivo = nr_seq_motivo_inativo_p
	where	nr_sequencia = nr_seq_agenda_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
