SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pcs_iniciar_finalizar_os ( nr_sequencia_p bigint, ie_opcao_p text, nr_seq_motivo_p bigint, nm_usuario_p text) AS $body$
BEGIN
 
if (ie_opcao_p = 'I') then 
	begin 
	CALL pcs_gerar_lista_analise(nr_sequencia_p,nm_usuario_p);	
	update	man_ordem_servico 
	set 	dt_inicio_real = clock_timestamp(), 
		dt_atualizacao = clock_timestamp(), 
		nm_usuario = nm_usuario_p, 
		ie_status_ordem = 2 
	where	nr_sequencia = nr_sequencia_p;	
	end;
elsif (ie_opcao_p = 'F') then 
	begin 
	update	man_ordem_servico 
	set 	dt_fim_real = clock_timestamp(), 
			dt_atualizacao = clock_timestamp(), 
			nr_seq_motivo_pcs = nr_seq_motivo_p, 
			nm_usuario = nm_usuario_p, 
		ie_status_ordem = 3 
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;	
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
