CREATE OR REPLACE PROCEDURE pcs_ini_fim_analise_item ( nr_seq_registro_p bigint, cd_material_p bigint, nr_seq_motivo_p bigint, ie_opcao_p text, nr_ordem_servico_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_ultimo_item_finalizado_w		integer;

/*
ie_opcao_p
I - Iniciar análise do item
F - Finalizar análise do item
U - Ultimo item finalizado - Além de finalizar o item, a lista será liberada, e será encerrado a OS.
D - Desfazer analise do item
*/
BEGIN

if (ie_opcao_p = 'I') then
	update	pcs_reg_analise_itens
	set		dt_inicio_analise = clock_timestamp(),
			nm_usuario_inicio_an = nm_usuario_p,
			ie_status_analise = 'E'
	where	nr_seq_registro = nr_seq_registro_p
	and		cd_material = cd_material_p;
elsif (ie_opcao_p = 'F') then
	update	pcs_reg_analise_itens
	set		dt_fim_analise  = clock_timestamp(),
			nm_usuario_fim_an = nm_usuario_p,
			ie_status_analise = 'A'
	where	nr_seq_registro = nr_seq_registro_p
	and		cd_material = cd_material_p;
elsif (ie_opcao_p = 'U') then
	update	pcs_reg_analise_itens
	set		dt_fim_analise  = clock_timestamp(),
			nm_usuario_fim_an = nm_usuario_p,
			ie_status_analise = 'A'
	where	nr_seq_registro = nr_seq_registro_p
	and		cd_material = cd_material_p;

	update	pcs_reg_analise
	set		dt_liberacao = clock_timestamp(),
			nm_usuario_lib = nm_usuario_p
	where	nr_sequencia = nr_seq_registro_p;

	if (nr_seq_motivo_p > 0) then
		update	man_ordem_servico
		set 	nr_seq_motivo_pcs = nr_seq_motivo_p
		where	nr_sequencia = nr_ordem_servico_p;
	end if;

	CALL pcs_encerrar_os(nr_ordem_servico_p,nm_usuario_p);
elsif (ie_opcao_p = 'D') then
	update	pcs_reg_analise_itens
	set		dt_fim_analise   = NULL,
			nm_usuario_fim_an  = NULL,
			dt_inicio_analise   = NULL,
			nm_usuario_inicio_an  = NULL,
			ie_status_analise = 'P'
	where	nr_seq_registro = nr_seq_registro_p
	and		cd_material = cd_material_p
	and	coalesce(nr_emprestimo::text, '') = ''
	and	coalesce(nr_ordem_compra::text, '') = ''
	and	coalesce(nr_solic_compra::text, '') = '';

	update	pcs_reg_analise_itens
	set		vl_informacao = 'null'
	where	nr_seq_registro = nr_seq_registro_p
	and		cd_material = cd_material_p
	and	coalesce(nr_emprestimo::text, '') = ''
	and	coalesce(nr_ordem_compra::text, '') = ''
	and	coalesce(nr_solic_compra::text, '') = ''
	and (nm_informacao in ('QT_MATERIAL','QT_EMPRESTIMO','QT_TRANSF','NR_SEQ_MOTIVO_SOLIC','IE_TIPO_COMPRA','CD_LOCAL_EST','CD_CONDICAO_PAG'));

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
