CREATE OR REPLACE PROCEDURE pcs_inserir_responsaveis ( nr_seq_regra_p bigint, cd_responsavel_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w integer;
nr_seq_regra_resp_w	bigint;

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pcs_regra_atividades a,
		pcs_regra_atividades_resp b
where	a.nr_sequencia = b.nr_seq_regra
and		a.nr_sequencia = nr_seq_regra_p
and		b.cd_responsavel = cd_responsavel_p;

if (coalesce(qt_existe_w,0) = 0) then

	select	nextval('pcs_regra_atividades_resp_seq')
	into STRICT	nr_seq_regra_resp_w
	;

insert into pcs_regra_atividades_resp(
		cd_responsavel,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_regra,
		nr_sequencia)
values (	cd_responsavel_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_regra_p,
		nr_seq_regra_resp_w);

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
