CREATE OR REPLACE PROCEDURE pcs_liberar_estornar_registro ( nr_seq_registro_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = 'L') then
	update	pcs_reclassif_curva
	set		dt_liberacao = clock_timestamp(),
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			nm_usuario_lib = nm_usuario_p
	where	nr_sequencia = nr_seq_registro_p;
elsif (ie_opcao_p = 'E') then
	update	pcs_reclassif_curva
	set		dt_liberacao  = NULL,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			nm_usuario_lib  = NULL
	where	nr_sequencia = nr_seq_registro_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
