CREATE OR REPLACE PROCEDURE pcs_mult_grupamento_reclassif (nr_seq_grupamento_p bigint, nr_seq_reclassif_p bigint, cd_empresa_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w	bigint;
nr_sequencia_w	bigint;
nr_seq_segmento_w bigint;
ie_curva_grup_w	varchar(1);
nr_seq_classif_grup_w bigint;
cd_estabelecimento_w	pcs_estrutura_segmento.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	c.cd_material,
		b.nr_sequencia,
		c.cd_estabelecimento
	from	pcs_grupamentos a,
		pcs_segmento b,
		pcs_estrutura_segmento c
	where	a.nr_sequencia = b.nr_seq_grupamento
	and	b.nr_seq_grupamento = c.nr_seq_grupamento
	and	b.nr_sequencia = c.nr_seq_segmento
	and	a.nr_sequencia = nr_seq_grupamento_p
	and	a.cd_empresa = cd_empresa_p;


BEGIN

select	coalesce(ie_curva_abc,'T') ie_curva_abc
into STRICT	ie_curva_grup_w
from	pcs_grupamentos
where	nr_sequencia = nr_seq_grupamento_p;

select	nextval('pcs_reclassif_curva_grup_seq')
into STRICT	nr_seq_classif_grup_w
;


insert into pcs_reclassif_curva_grup(
	dt_atualizacao,
	dt_atualizacao_nrec,
	ie_curva_abc_grup,
	nm_usuario,
	nm_usuario_nrec,
	nr_seq_grupamento,
	nr_seq_reclassif,
	nr_sequencia)
values (	clock_timestamp(),
	clock_timestamp(),
	ie_curva_grup_w,
	nm_usuario_p,
	nm_usuario_p,
	nr_seq_grupamento_p,
	nr_seq_reclassif_p,
	nr_seq_classif_grup_w);

open C01;
loop
fetch C01 into
	cd_material_w,
	nr_seq_segmento_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nextval('pcs_reclassif_curva_item_seq')
	into STRICT	nr_sequencia_w
	;

	insert into pcs_reclassif_curva_item(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_classif_grup,
		nr_seq_segmento,
		ie_divergente,
		ie_curva_abc_orig,
		cd_material,
		cd_estabelecimento)
	values (	nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_classif_grup_w,
		nr_seq_segmento_w,
		'N',
		ie_curva_grup_w,
		cd_material_w,
		cd_estabelecimento_w);
	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
