CREATE OR REPLACE PROCEDURE pda_finalizar_cont_item_inv ( nr_sequencia_p bigint, nr_seq_inventario_p bigint, qt_contagem_p bigint, nm_usuario_p text, nr_seq_lote_p bigint default null) AS $body$
DECLARE


cd_estabelecimento_w		integer;
ie_contagem_atual_w		varchar(15);
nm_usuario_contagem_atual_w	varchar(15);
ds_consiste_inv_w			varchar(5);
qt_inventario_w			double precision;
cd_material_w			integer;
ds_erro_w			varchar(255);


BEGIN

ds_consiste_inv_w := substr(coalesce(obter_valor_param_usuario(88,80, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento),'N'),1,1);

select	coalesce(max(ie_contagem_atual), '1'),
	max(cd_material)
into STRICT	ie_contagem_atual_w,
	cd_material_w
from	inventario_material
where	nr_seq_inventario = nr_seq_inventario_p
and	nr_sequencia = nr_sequencia_p;

select	coalesce(CASE WHEN ie_contagem_atual_w=1 THEN 	nm_usuario_contagem WHEN ie_contagem_atual_w=2 THEN 	nm_usuario_recontagem WHEN ie_contagem_atual_w=3 THEN 	nm_usuario_seg_recontagem WHEN ie_contagem_atual_w=4 THEN 	nm_usuario_terc_recontagem END ,nm_usuario_p),
	qt_inventario
into STRICT	nm_usuario_contagem_atual_w,
	qt_inventario_w
from	inventario_material
where	nr_sequencia  = nr_sequencia_p;

if (qt_inventario_w IS NOT NULL AND qt_inventario_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(198942);
	-- A quantidade deste item no invent치rio j치 est치 definida. Esta leitura ser치 desconsiderada.
end if;

ds_erro_w := Inventario_barras(nr_seq_inventario_p, cd_material_w, qt_contagem_p, nr_seq_lote_p, ie_contagem_atual_w, nm_usuario_p, ds_erro_w);

if (coalesce(ds_erro_w,'X') <> 'X') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_erro_w);
end if;

update	inventario_material
set	ie_status_inventario	= 'C'
where	nr_sequencia		= nr_sequencia_p;


/* Retira o item do bloqueio */

delete	FROM inventario_bloqueio
where	nr_seq_inventario  	= nr_seq_inventario_p
and	nr_seq_item 	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
