CREATE OR REPLACE PROCEDURE pda_finalizar_protocolo_ccg ( nr_prescricao_p bigint, nr_seq_procedimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_item_assoc_w		integer;
						
C01 CURSOR FOR  --Buscar itens associados ao procedimento 
SELECT	nr_sequencia 
from	prescr_material 
where	nr_prescricao = nr_prescricao_p 
and	nr_sequencia_proc = nr_seq_procedimento_p 
and	coalesce(dt_suspensao::text, '') = '';
						

BEGIN 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') then 
	 
	CALL suspender_hor_item_prescr(nr_prescricao_p, nr_seq_procedimento_p, 'PRESCR_PROCEDIMENTO', nm_usuario_p,'S','N');
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_item_assoc_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */		
		CALL suspender_hor_item_prescr(nr_prescricao_p, nr_seq_item_assoc_w, 'PRESCR_MATERIAL', nm_usuario_p,'S','N');		
	end loop;
	close C01;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
