CREATE OR REPLACE PROCEDURE pde_enviar_integracao (NR_SEQ_PDE_MAIN_P bigint, IE_OPCAO_P text DEFAULT 'T') AS $body$
DECLARE

/*
  - IE_OPCAO_P:
    T - Gerar e enviar o Json
    J - Gerar Json
    E - Enviar Json
*/
BEGIN
  IF (coalesce(IE_OPCAO_P, 'T') IN ('T', 'J')) THEN
    CALL PDE.PROCESSA_DADOS_PDE_MAIN(NR_SEQ_PDE_MAIN_P);
  END IF;

  IF (coalesce(IE_OPCAO_P, 'T') IN ('T', 'E')) THEN
    CALL PDE.ENVIAR_INTEGRACAO_BY_PDE_MAIN(NR_SEQ_PDE_MAIN_P);
  END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
