CREATE OR REPLACE PROCEDURE pepa_gerar_inf_req_paciente ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nm_usuario_p text) AS $body$
DECLARE


nr_seq_atend_cons_pepa_w ATEND_CONSULTA_PEPA.nr_sequencia%type;

c01 CURSOR FOR
SELECT nr_sequencia
from ATEND_CONSULTA_PEPA
where (nr_atendimento = nr_atendimento_p
	or (coalesce(nr_atendimento_p::text, '') = '' and cd_pessoa_fisica = cd_pessoa_fisica_p))
	and coalesce(dt_liberacao::text, '') = '';


BEGIN

open c01;
loop
fetch c01
into nr_seq_atend_cons_pepa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	CALL PEPA_GERAR_INFORMACAO_REQ(nr_seq_atend_cons_pepa_w, nm_usuario_p);

	end;
end loop;
close c01;

end;
$body$
