CREATE OR REPLACE PROCEDURE pepo_atualizar_status_doc ( nr_cirurgia_p pepo_doc_status.nr_cirurgia%type, cd_item_p pepo_doc_status.cd_item%type, ie_status_p pepo_doc_status.ie_status%type, nm_usuario_p pepo_doc_status.nm_usuario%type, nr_seq_pepo_p pepo_doc_status.nr_seq_pepo%type) AS $body$
BEGIN
    if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
        update  pepo_doc_status
        set     ie_status = ie_status_p,
                nm_usuario = nm_usuario_p,
                dt_atualizacao = clock_timestamp()
        where   nr_cirurgia = nr_cirurgia_p
        and     cd_item = cd_item_p;
        commit;
    end if;

    if ((nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') and coalesce(nr_cirurgia_p::text, '') = '') then 
        update  pepo_doc_status
        set     ie_status = ie_status_p,
                nm_usuario = nm_usuario_p,
                dt_atualizacao = clock_timestamp()
        where   nr_seq_pepo = nr_seq_pepo_p
        and     cd_item = cd_item_p
		and     coalesce(nr_cirurgia::text, '') = '';
        commit;
    end if;

end;
$body$
