CREATE OR REPLACE PROCEDURE pepo_consistir_evento_cir_pac ( cd_profissional_p evento_cirurgia_paciente.cd_profissional%type, nr_cirurgia_p evento_cirurgia_paciente.nr_cirurgia%type, nm_usuario_p evento_cirurgia_paciente.nm_usuario%type, nr_seq_pepo_p evento_cirurgia_paciente.nr_seq_pepo%type, cd_local_estoque_p material_atend_paciente.cd_local_estoque%type, ie_liberar_p text ) AS $body$
DECLARE


nr_sequencia_w evento_cirurgia_paciente.nr_sequencia%type := null;

c01 CURSOR FOR
    SELECT  nr_sequencia,
            nr_seq_evento,
            dt_inicio_evento
    from    w_evento_cirurgia_paciente
    where   coalesce(nr_cirurgia, 0) = coalesce(nr_cirurgia_p, 0)
    and     coalesce(nr_seq_pepo, 0) = coalesce(nr_seq_pepo_p, 0)
    and     nm_usuario = nm_usuario_p;

BEGIN
    for vet in c01 loop
        nr_sequencia_w := gerar_evento_cirurgia(
            cd_profissional_p, clock_timestamp(), nr_cirurgia_p, vet.nr_seq_evento, cd_local_estoque_p, null, nm_usuario_p, nr_sequencia_w, nr_seq_pepo_p, vet.dt_inicio_evento
        );

        update  w_evento_cirurgia_paciente
        set     nr_seq_evento_cirurgia_pac = nr_sequencia_w
        where   nr_sequencia = vet.nr_sequencia;

        if (ie_liberar_p = 'S') then
            CALL liberar_informacao('EVENTO_CIRURGIA_PACIENTE', 'NR_SEQUENCIA', nr_sequencia_w, nm_usuario_p);
        end if;
    end loop;

    commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
