CREATE OR REPLACE PROCEDURE pepo_deletar_w_evento_cir_pac ( nr_cirurgia_p w_evento_cirurgia_paciente.nr_cirurgia%type, nr_seq_pepo_p w_evento_cirurgia_paciente.nr_seq_pepo%type, nm_usuario_p w_evento_cirurgia_paciente.nm_usuario%type, nr_sequencia_p w_evento_cirurgia_paciente.nr_sequencia%type ) AS $body$
BEGIN
    if (coalesce(nr_sequencia_p::text, '') = '') then
        delete
        from    w_evento_cirurgia_paciente
        where   coalesce(nr_cirurgia, 0) = coalesce(nr_cirurgia_p, 0)
        and     coalesce(nr_seq_pepo, 0) = coalesce(nr_seq_pepo_p, 0)
        and     nm_usuario = nm_usuario_p;
    else
        delete
        from    w_evento_cirurgia_paciente
        where   nr_sequencia = nr_sequencia_p;
    end if;

    commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
