SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pepo_generate_unsign (NR_SEQUENCIA_P bigint, DS_JUSTIFICATIVA_P text) AS $body$
DECLARE


pending_approvals bigint;
nr_seq_doc_t bigint;
all_approvals bigint;


BEGIN
    select NR_SEQ_DOC into STRICT nr_seq_doc_t from documento_aprov_prof where nr_sequencia = NR_SEQUENCIA_P;

    UPDATE documento_aprov_prof
    SET
        DT_RET_APROVACAO = clock_timestamp(),
        DS_JUST_RET_APROV = DS_JUSTIFICATIVA_P,
        IE_STATUS = 'R',
        DT_APROVACAO  = NULL
    WHERE
        NR_SEQUENCIA = NR_SEQUENCIA_P;

    select count(*) into STRICT all_approvals from documento_aprov_prof where NR_SEQ_DOC = nr_seq_doc_t;
    select count(*) into STRICT pending_approvals from documento_aprov_prof where NR_SEQ_DOC = nr_seq_doc_t and ie_status <> 'A';

    if (pending_approvals = all_approvals) then 
        update documento_aprovacao
        set
            IE_STATUS = 'N'
        where
            nr_sequencia = nr_seq_doc_t;
            commit;
    else
        update documento_aprovacao
        set
            IE_STATUS = 'P'
        where
            nr_sequencia = nr_seq_doc_t;
            commit;
    end if;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
