CREATE OR REPLACE PROCEDURE pepo_temp_mov_eve_cir_aftpost ( nr_cirurgia_atual_p bigint, nr_procedimento_atual_p bigint, ie_estrutura_PEPO_p text, ie_nivel_cirurgia_p text, nr_seq_aten_pac_unid_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE

					 
nr_cirurgia_w		bigint;
nr_seq_pepo_w		bigint;
ie_gera_exclui_tempo_w	varchar(1):= 'N';
					

BEGIN 
 
ie_gera_exclui_tempo_w := obter_param_usuario(872, 345, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_gera_exclui_tempo_w);
 
if (ie_gera_exclui_tempo_w = 'S') then 
	begin 
	 
	delete	from tempo_cirurgico_cirurgia 
	where	nr_cirurgia = nr_procedimento_atual_p;
 
	delete	from tempo_cirurgico_cirurgia 
	where	nr_seq_pepo = nr_cirurgia_atual_p;
		 
	if (ie_estrutura_PEPO_p = 'S') and (ie_nivel_cirurgia_p = 'S') then 
		begin 
		nr_seq_pepo_w := nr_cirurgia_atual_p;
		end;
	else 
		begin 
		nr_cirurgia_w := nr_procedimento_atual_p;
		end;
	end if;
	 
	end;
	 
	CALL gerar_tempo_cirurgico(nr_cirurgia_w,nm_usuario_p,nr_seq_pepo_w);
	 
end if;
 
if (nr_seq_aten_pac_unid_p IS NOT NULL AND nr_seq_aten_pac_unid_p::text <> '') then 
	begin 
	CALL atualiza_mov_evento_cirurgia(nr_seq_aten_pac_unid_p);
	end;
end if;
 
commit;
 
end;
$body$
