CREATE OR REPLACE PROCEDURE pep_add_fav_problem_list ( nm_usuario_p PEP_FAV_PROBLEMA.nm_usuario%type, nr_sequencia_p LISTA_PROBLEMA_PAC.nr_sequencia%type) AS $body$
DECLARE


nr_sequencia_w	PEP_FAV_PROBLEMA.nr_sequencia%type;

nr_seq_apresentacao_w	PEP_FAV_PROBLEMA.nr_sequencia%type;


BEGIN

select	coalesce(max(nr_sequencia),0)+1,
        coalesce(max(nr_seq_apresentacao),0)+1
into STRICT	nr_sequencia_w,
        nr_seq_apresentacao_w
from	PEP_FAV_PROBLEMA;


insert into PEP_FAV_PROBLEMA(
				NR_SEQUENCIA,
				DT_ATUALIZACAO,
				NM_USUARIO,
				DT_ATUALIZACAO_NREC,
				NM_USUARIO_NREC,
				CD_DOENCA,
				DS_PROBLEMA,
                DS_PROBLEMA_FAVLIST,
				IE_TIPO_DIAGNOSTICO,
				NR_SEQ_CAD_PROBLEMA,
				NR_SEQ_CATEGORIA,
				NR_SEQ_HISTORICO,
				NR_SEQ_TIPO_HIST,
                NR_SEQ_APRESENTACAO
				)
			(SELECT	nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				CD_DOENCA,
				DS_PROBLEMA,
                DS_PROBLEMA,
				IE_TIPO_DIAGNOSTICO,
				NR_SEQ_CAD_PROBLEMA,
				NR_SEQ_CATEGORIA,
				NR_SEQ_HISTORICO,
