CREATE OR REPLACE PROCEDURE pep_altera_peso_altura ( qt_altura_p bigint, qt_peso_p bigint, nr_seq_paciente_p bigint, nm_usuario_p text, ie_opcao_p text default 'P') AS $body$
DECLARE


/*
Paciente setor			P
Atendimento do ciclo		A
*/
BEGIN

if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then

	if (ie_opcao_p = 'A') then


		update paciente_atendimento
		set		qt_altura = qt_altura_p,
				qt_peso   = qt_peso_p,
				dt_atualizacao = clock_timestamp(),
				nm_usuario     = nm_usuario_p
		where 	nr_seq_atendimento = 		nr_seq_paciente_p;

	elsif (ie_opcao_p = 'P') then


		update 	paciente_setor
		set		qt_altura = qt_altura_p,
				qt_peso   = qt_peso_p,
				dt_atualizacao = clock_timestamp(),
				nm_usuario     = nm_usuario_p
		where	nr_seq_paciente = nr_seq_paciente_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
