CREATE OR REPLACE PROCEDURE pep_atualizar_class_auto ( nr_seq_triagem_p bigint, nr_atendimento_p bigint, nm_usuario_p text, ds_justificativa_p text default null) AS $body$
DECLARE


          
ds_erro_w    varchar(255);
nr_seq_pa_w    triagem_pronto_atend.nr_sequencia%type;


BEGIN

if (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then
  CALL inserir_obs_log_urgencia(ds_justificativa_p);
end if;

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_triagem_p IS NOT NULL AND nr_seq_triagem_p::text <> '') then
  begin

  update   atendimento_paciente
  set    nr_seq_triagem     = nr_seq_triagem_p
  where  nr_atendimento  = nr_atendimento_p;

  Select  max(nr_sequencia)
  into STRICT  nr_seq_pa_w  
  from  triagem_pronto_atend
  where   nr_atendimento = nr_atendimento_p;

  if (nr_seq_pa_w IS NOT NULL AND nr_seq_pa_w::text <> '') then
  
    update   triagem_pronto_atend
    set    nr_seq_classif   = nr_seq_triagem_p
    where  nr_sequencia = nr_seq_pa_w;

    begin    
      CALL gerar_log_triagem_pa(nr_atendimento_p,'C',nm_usuario_p, nr_seq_triagem_p, 'N', ds_justificativa_p, nr_seq_pa_w);
    exception
    when others then
      ds_erro_w  := substr(sqlerrm,1,255);
    end;

  end if;

  
  end;
end if;


commit;

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_triagem_p IS NOT NULL AND nr_seq_triagem_p::text <> '') then
  begin
  begin
    CALL gerar_lancamento_automatico(nr_atendimento_p, null, 547, nm_usuario_p, 0, null, null, null, null, null);
  exception
  when others then
    ds_erro_w  := substr(sqlerrm,1,255);
  end;
  end;
end if;


