CREATE OR REPLACE PROCEDURE pep_atualiza_datas_mentor (nr_seq_pend_pac_acao_dest_p bigint) AS $body$
BEGIN

if (coalesce(nr_seq_pend_pac_acao_dest_p, 0) > 0) then

    update gqa_pend_pac_acao_dest
    set    dt_ciencia	      = clock_timestamp(), 
           dt_encerramento  = clock_timestamp() 
    where  nr_sequencia     = nr_seq_pend_pac_acao_dest_p;

    commit;

end if;
    
end;
$body$
