CREATE OR REPLACE PROCEDURE pep_atualiza_dt_fim_medic ( nr_sequencia_p bigint, dt_fim_p timestamp, ds_observacao_p text, ie_nao_sabe_fim_p text, ie_atualiza_tudo_p text, nm_usuario_p text ) AS $body$
BEGIN

if (ie_atualiza_tudo_p = 'S') then
	begin

	update   paciente_medic_uso
	set      dt_fim         = dt_fim_p
        where    nr_sequencia   = nr_sequencia_p;

	update   paciente_medic_uso
	set      ie_nao_sabe_fim = ie_nao_sabe_fim_p,
                 ds_observacao   = substr(ds_observacao||CASE WHEN ds_observacao = NULL THEN null  ELSE chr(13)||chr(10) END ||ds_observacao_p,1,255)
        where    nr_sequencia    = nr_sequencia_p;

	end;
else
	begin

	update   paciente_medic_uso
	set      ie_nao_sabe_fim = ie_nao_sabe_fim_p,
                 ds_observacao   = substr(ds_observacao||CASE WHEN ds_observacao = NULL THEN null  ELSE chr(13)||chr(10) END ||ds_observacao_p,1,255)
        where    nr_sequencia    = nr_sequencia_p;

	end;
end if;

commit;

end;
