CREATE OR REPLACE PROCEDURE pep_atualiza_medic_ciente_aud ( lista_nr_atendimento_p text, cd_medico_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_nr_atendimento_w	varchar(2000) := '';
k				integer;
ds_nr_atendimento_w		varchar(2000) := '';
nr_atendimento_w		bigint;



BEGIN

ds_lista_nr_atendimento_w := lista_nr_atendimento_p;

while (ds_lista_nr_atendimento_w IS NOT NULL AND ds_lista_nr_atendimento_w::text <> '') loop
	begin

	select	position(',' in ds_lista_nr_atendimento_w)
	into STRICT	k
	;

	if (k > 1) and ((substr(ds_lista_nr_atendimento_w, 1, k -1) IS NOT NULL AND (substr(ds_lista_nr_atendimento_w, 1, k -1))::text <> '')) then
		ds_nr_atendimento_w		:= substr(ds_lista_nr_atendimento_w, 1, k-1);
		nr_atendimento_w		:= replace(ds_nr_atendimento_w, ',','');
		ds_lista_nr_atendimento_w	:= substr(ds_lista_nr_atendimento_w, k + 1, 2000);
	elsif (ds_lista_nr_atendimento_w IS NOT NULL AND ds_lista_nr_atendimento_w::text <> '') then
		nr_atendimento_w		:= replace(ds_lista_nr_atendimento_w,',','');
		ds_lista_nr_atendimento_w	:= '';
	end if;

	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
		update	atendimento_audit_medica
		set	dt_medico_assistente	= clock_timestamp()
		where	coalesce(dt_medico_assistente::text, '') = ''
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	nr_atendimento		= nr_atendimento_w
		and	cd_destinatario		= cd_medico_p;

		commit;
	end if;

	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
