SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pep_chamar_pac_enfermagem ( nr_atendimento_p bigint, ie_opcao_p text default 'E') AS $body$
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	if (coalesce(ie_opcao_p,'E')	= 'E') then

		update 	atendimento_paciente
		set 	dt_chamada_enfermagem 	= clock_timestamp()
		where 	nr_atendimento 		= nr_atendimento_p;

	elsif (coalesce(ie_opcao_p,'E')	= 'C') then
		update 	atendimento_paciente
		set 	dt_chamada_enfermagem 	 = NULL
		where 	nr_atendimento 		= nr_atendimento_p;

	elsif (coalesce(ie_opcao_p,'H')	= 'H') then

		update 	atendimento_paciente
		set 	dt_chamada_enfermagem 	= clock_timestamp(),
				ie_chamado = 'S'
		where 	nr_atendimento 		= nr_atendimento_p;

	end if;
end if;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
