CREATE OR REPLACE PROCEDURE pep_definir_diag_entrada ( nr_sequencia_p DIAGNOSTICO_DOENCA.NR_SEQ_INTERNO%type, nm_usuario_p DIAGNOSTICO_DOENCA.NM_USUARIO%type, ie_situacao_p DIAGNOSTICO_DOENCA.IE_SITUACAO%type, dt_liberacao_p DIAGNOSTICO_DOENCA.DT_LIBERACAO%type ) AS $body$
BEGIN
    IF (ie_situacao_p = 'A' AND (dt_liberacao_p IS NOT NULL AND dt_liberacao_p::text <> '')) THEN

        UPDATE DIAGNOSTICO_DOENCA
        SET    IE_DIAG_ADMISSAO    = 'S',
               NM_USUARIO          = nm_usuario_p,
               DT_ATUALIZACAO      = clock_timestamp()
        WHERE  NR_SEQ_INTERNO      = nr_sequencia_p;

        COMMIT;
    END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
