CREATE OR REPLACE PROCEDURE pep_excluir_prescricao_js ( nr_prescricao_p bigint, nm_usuario_original_p text, nm_usuario_p text ) AS $body$
DECLARE


vl_prescr_medica_w	bigint;
qt_prescr_material_w	bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin

	if (nm_usuario_p <> nm_usuario_original_p) then
		begin
		--(-20011, substr(obter_texto_tasy (60521, wheb_usuario_pck.get_nr_seq_idioma),1,255) || '#@#@');
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(60521);
		end;
	end if;

	select	1
	into STRICT	vl_prescr_medica_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p
	and	coalesce(dt_liberacao::text, '') = ''
	and	coalesce(dt_liberacao_medico::text, '') = '';

	if (vl_prescr_medica_w <> 1) then
		begin
		--(-20011, substr(obter_texto_tasy (60522, wheb_usuario_pck.get_nr_seq_idioma),1,255) || '#@#@');
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(60522);
		end;
	else
		begin

		select	count(*)
		into STRICT	qt_prescr_material_w
		from	prescr_material
		where	nr_prescricao = nr_prescricao_p
		and	((dt_lib_material IS NOT NULL AND dt_lib_material::text <> '')
		or (dt_lib_enfermagem IS NOT NULL AND dt_lib_enfermagem::text <> '') or (dt_lib_farmacia IS NOT NULL AND dt_lib_farmacia::text <> ''));

		if (qt_prescr_material_w > 0) then
			begin
			--(-20011, substr(obter_texto_tasy (60524, wheb_usuario_pck.get_nr_seq_idioma),1,255) || '#@#@');
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(60524);
			end;
		end if;

		end;
	end if;


	begin
	delete from prescr_medica where nr_prescricao = nr_prescricao_p;
	exception
	when others then
		qt_prescr_material_w	:= 0;
	end;

	commit;

	end;
end if;


end;
$body$
