CREATE OR REPLACE PROCEDURE pep_gerar_motivo_lib_precaucao ( dt_liberacao_p timestamp, nr_seq_precaucao_p bigint, nr_atendimento_p bigint, nr_seq_motivo_isol_p bigint, nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE

 
ie_isolamento_w			varchar(1) := '';
ie_gerar_motivo_lib_prec_w	varchar(1) := '';
ie_fim_isolamento_w		varchar(1) := '';
		

BEGIN 
 
ie_gerar_motivo_lib_prec_w := obter_param_usuario(281, 955, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_gerar_motivo_lib_prec_w);
 
select	ie_isolamento 
into STRICT	ie_isolamento_w 
from	cih_precaucao 
where	nr_sequencia = nr_seq_precaucao_p;
 
select	coalesce(max('S'), 'N') 
into STRICT	ie_fim_isolamento_w 
from	atendimento_precaucao 
where	nr_sequencia = nr_sequencia_p 
and   coalesce(dt_termino::text, '') = '' 
and   coalesce(dt_inativacao::text, '') = '';
 
if (ie_fim_isolamento_w = 'S') and (dt_liberacao_p IS NOT NULL AND dt_liberacao_p::text <> '') and (ie_isolamento_w = 'S') then 
	begin 
	 
	update	atendimento_paciente 
    set	ie_paciente_isolado = 'S', 
		dt_atualizacao = clock_timestamp() 
	where	nr_atendimento = nr_atendimento_p;
	 
	 
	if (ie_gerar_motivo_lib_prec_w = 'S') and (nr_seq_motivo_isol_p IS NOT NULL AND nr_seq_motivo_isol_p::text <> '') then 
		begin 
		 
		CALL gerar_motivo_isolamento(nr_atendimento_p,nr_seq_motivo_isol_p,nm_usuario_p,'I',null,null,null,clock_timestamp(),null,nr_sequencia_p,nr_sequencia_p);
		 
		end;
	end if;
	 
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
