CREATE OR REPLACE PROCEDURE pep_gerar_pedido_exame_ext ( nr_seq_pedido_p bigint, lista_nr_seq_exame_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
nr_seq_apresent_w	bigint;
qt_exame_w		integer;
ds_justificativa_w	varchar(4000);
nr_seq_exame_w		bigint;
ds_lista_exames_w	varchar(2000);
ds_lista_aux_w		varchar(2000) := '';
k			integer;


BEGIN 
ds_lista_exames_w := lista_nr_seq_exame_p;
while (ds_lista_exames_w IS NOT NULL AND ds_lista_exames_w::text <> '') loop 
	begin 
	 
	select	position(',' in ds_lista_exames_w) 
	into STRICT	k 
	;
 
	if (k > 1) and ((substr(ds_lista_exames_w, 1, k -1) IS NOT NULL AND (substr(ds_lista_exames_w, 1, k -1))::text <> '')) then 
		ds_lista_aux_w			:= substr(ds_lista_exames_w, 1, k-1);
		nr_seq_exame_w			:= replace(ds_lista_aux_w, ',','');
		ds_lista_exames_w		:= substr(ds_lista_exames_w, k + 1, 2000);
	elsif (ds_lista_exames_w IS NOT NULL AND ds_lista_exames_w::text <> '') then 
		nr_seq_exame_w			:= replace(ds_lista_exames_w,',','');
		ds_lista_exames_w		:= '';
	end if;
	 
	CALL Gerar_pedido_exame_ext_item(nr_seq_pedido_p,nr_seq_exame_w,nm_usuario_p);
	 
	end;
end loop;
	 
commit;
 
end;
$body$
