SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pep_gerar_token_acesso (nm_usuario_p text, nr_atendimento_p bigint, cd_pessoa_fisica_p text, dt_inicio_p timestamp default null, cd_token_p INOUT text DEFAULT NULL, nr_prescricao_p bigint default null, nr_transcricao_p bigint default null) AS $body$
DECLARE

qt_reg_w	bigint;
ds_comando_w	varchar(2000);
ds_parametro_w	varchar(2000);
ds_sep_bv_w	varchar(10);
nr_atendimento_w	bigint;
nr_prescricao_w   bigint;
cd_pessoa_fisica_w	varchar(10);


BEGIN 
 
select	count(*) 
into STRICT	qt_reg_w 
from	user_tables 
where	table_name = 'CUS_SIH_TOKENS';
 
nr_atendimento_w := nr_atendimento_p;
 
if (nr_atendimento_p > 0) then 
 
	SELECT 	max(CD_PESSOA_FISICA) 
	INTO STRICT 	cd_pessoa_fisica_w 
	FROM 	atendimento_paciente 
	WHERE 	NR_ATENDIMENTO = NR_ATENDIMENTO_P;
 
	if (cd_pessoa_fisica_w <> cd_pessoa_fisica_p) then	 
	 
		nr_atendimento_w := 0;
	 
	end if;
 
 
end if;	
 
 
if (qt_reg_w	> 0) then 
 
	select 	CASE WHEN wheb_usuario_pck.get_cd_funcao=3112 THEN null  ELSE nr_prescricao_p END  
	into STRICT  	nr_prescricao_w 
	;
	ds_sep_bv_w	:= OBTER_SEPARADOR_BV;
	cd_token_p	:= uuid_generate_v4();
	ds_comando_w	:= '	insert into CUS_SIH_TOKENS (TOKEN,LOGIN,ATENDIMENTO,cd_pessoa_fisica,nr_prescricao,dt_inicio, nr_transcricao, cd_funcao) values (:TOKEN,:NM_USUARIO,:ATENDIMENTO,:cd_pessoa_fisica,:nr_prescricao,:dt_inicio, :nr_transcricao, :cd_funcao)';
	ds_parametro_w	:= 'TOKEN='||cd_token_p||ds_sep_bv_w|| 
			  'ATENDIMENTO='||nr_atendimento_w||ds_sep_bv_w|| 
			  'cd_pessoa_fisica='||cd_pessoa_fisica_p||ds_sep_bv_w|| 
			  'nr_prescricao='|| nr_prescricao_w ||ds_sep_bv_w|| 
			  'nr_transcricao='||nr_transcricao_p||ds_sep_bv_w|| 
			  'NM_USUARIO='||nm_usuario_p||ds_sep_bv_w|| 
			  'dt_inicio='||TO_CHAR(dt_inicio_p,'dd/mm/yyyy hh24:mi:ss')||ds_sep_bv_w || 
			  'cd_funcao='|| wheb_usuario_pck.get_cd_funcao;
			  
			  
	CALL Exec_sql_Dinamico_bv('TOKEN',ds_comando_w,ds_parametro_w);
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
