SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pep_gravar_log_age_cons ( nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_profissional_w		varchar(10);
ie_nivel_Atencao_w		atendimento_paciente.ie_nivel_Atencao%type;


BEGIN
if (coalesce(nr_seq_agenda_p,0) > 0) then

	select	max(nr_atendimento),
			max(obter_pessoa_atendimento(nr_atendimento,'C'))
	into STRICT	nr_atendimento_w,
			cd_pessoa_fisica_w
	from	agenda_consulta_v2
	where	nr_sequencia = nr_seq_agenda_p;

	select	max(cd_pessoa_fisica)
	into STRICT	cd_profissional_w
	from	usuario
	where	upper(nm_usuario) = upper(nm_usuario_p);

	Select max(ie_nivel_Atencao)
	into STRICT	ie_nivel_Atencao_w
	from	atendimento_paciente
	where	nr_Atendimento = nr_atendimento_w;

	if (ie_nivel_Atencao_w = 'P') then


	insert into atendimento_soap(
									nr_sequencia,
									dt_atualizacao,
									nm_usuario,
									dt_atualizacao_nrec,
									nm_usuario_nrec,
									cd_pessoa_fisica,
									nr_atendimento,
									ie_situacao,
									nr_seq_agenda_consulta,
									cd_profissional)
						values (		nextval('atendimento_soap_seq'),
									clock_timestamp(),
									nm_usuario_p,
									clock_timestamp(),
									nm_usuario_p,
									cd_pessoa_fisica_w,
									nr_atendimento_w,
									'A',
									nr_seq_agenda_p,
									cd_profissional_w);

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
