CREATE OR REPLACE PROCEDURE pep_inform_rn_gerar_alta_js ( nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_verifica_rn_p INOUT text, ie_gerar_alta_atend_rn_p INOUT text, ds_pergunta_p INOUT text, nr_atendimento_rn_p INOUT bigint ) AS $body$
DECLARE

		 
ie_verifica_rn_w		varchar(1) 	:= 'N';
ie_gerar_alta_atend_rn_w	varchar(1) 	:= 'N';
qt_atendimento_paciente_w	bigint	:= 0;
nr_atendimento_rn_w		bigint	:= 0;
		

BEGIN 
 
select count(nr_atendimento) 
into STRICT	qt_atendimento_paciente_w 
from  atendimento_paciente 
where  nr_atendimento_mae = nr_atendimento_p 
and	coalesce(dt_alta::text, '') = '';
 
select	max(nr_atendimento) 
into STRICT	nr_atendimento_rn_w 
from	atendimento_paciente 
where	nr_atendimento_mae = nr_atendimento_p 
and	coalesce(dt_alta::text, '') = '';
 
if (qt_atendimento_paciente_w > 0) then 
	begin 
	ie_verifica_rn_w := 'S';
	end;
end if;
 
select	coalesce(max(ie_gerar_alta_atend_rn),'S') 
into STRICT	ie_gerar_alta_atend_rn_w 
from	parametro_atendimento 
where	cd_estabelecimento = cd_estabelecimento_p;
 
ds_pergunta_p			:= substr(obter_texto_tasy(66232, wheb_usuario_pck.get_nr_seq_idioma),1,255);
ie_verifica_rn_p		:= ie_verifica_rn_w;
ie_gerar_alta_atend_rn_p	:= ie_gerar_alta_atend_rn_w;
nr_atendimento_rn_p		:= nr_atendimento_rn_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
