SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pep_inserir_gp_controle ( nr_atendimento_p bigint, list_nr_sequencia_p text, nm_usuario_p text) AS $body$
DECLARE


qt_hora_w	bigint;
ds_lista_w	varchar(2000);
ds_lista_aux_w	varchar(2000);
nr_sequencia_w	bigint;
k		integer;


BEGIN

ds_lista_w := list_nr_sequencia_p;

while (ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
	begin

	select	position(',' in ds_lista_w)
	into STRICT	k
	;

	if (k > 1) and ((substr(ds_lista_w, 1, k -1) IS NOT NULL AND (substr(ds_lista_w, 1, k -1))::text <> '')) then
		ds_lista_aux_w			:= substr(ds_lista_w, 1, k-1);
		nr_sequencia_w			:= replace(ds_lista_aux_w, ',','');
		ds_lista_w			:= substr(ds_lista_w, k + 1, 2000);
	elsif (ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') then
		nr_sequencia_w			:= replace(ds_lista_w,',','');
		ds_lista_w			:= '';
	end if;

	select	qt_horas_alerta
	into STRICT	qt_hora_w
	from	tipo_perda_ganho
	where	nr_sequencia	= nr_sequencia_w;

	insert into ATEND_PERDA_GANHO_CONTROLE(	nr_sequencia,
						nr_atendimento,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						ie_controla,
						nr_seq_tipo,
						qt_horas_alerta)
			values (	nextval('atend_perda_ganho_controle_seq'),
						nr_atendimento_p,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						'S',
						nr_sequencia_w,
						qt_hora_w);

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
