SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pep_inserir_local_metastase ( list_nr_sequencia_p text, nr_seq_interno_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_localizacao_metastase_w	varchar(80);
nr_sequencia_w			bigint;
ds_lista_w			varchar(2000) := '';
ds_lista_aux_w			varchar(2000) := '';
k				integer;
			

BEGIN 
ds_lista_w := list_nr_sequencia_p;
 
while (ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop 
	begin 
	 
	select	position(',' in ds_lista_w) 
	into STRICT	k 
	;
 
	if (k > 1) and ((substr(ds_lista_w, 1, k -1) IS NOT NULL AND (substr(ds_lista_w, 1, k -1))::text <> '')) then 
		ds_lista_aux_w			:= substr(ds_lista_w, 1, k-1);
		nr_sequencia_w			:= replace(ds_lista_aux_w, ',','');
		ds_lista_w			:= substr(ds_lista_w, k + 1, 2000);
	elsif (ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') then 
		nr_sequencia_w			:= replace(ds_lista_w,',','');
		ds_lista_w			:= '';
	end if;
 
	select	ds_localizacao_metastase 
	into STRICT	ds_localizacao_metastase_w 
	from	sus_localizacao_metastase 
	where	nr_sequencia	= nr_sequencia_w;
 
	update	sus_laudo_paciente 
	set	ds_localizacao_metastase	= substr(ds_localizacao_metastase_w || ' ,' || ds_localizacao_metastase ,1,80) 
	where	nr_seq_interno			= nr_seq_interno_p;
 
	end;
end loop;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
