CREATE OR REPLACE PROCEDURE pep_insert_diagnostico_hist (nr_atendimento_p bigint, ie_tipo_diagnostico_p bigint, nm_usuario_p text, ds_observacao_p text, nr_seq_interno_p diagnostico_doenca.nr_seq_interno%type) AS $body$
DECLARE


cd_status_w	varchar(3);


BEGIN

  -- Called From Wdlg 52419. Alter IE_TIPO_DIAGNOSTICO
        begin
                select  ie_status
                into STRICT    cd_status_w
                from    DIAGNOSTICO_STATUS
                where   ie_situacao = 'A'  LIMIT 1;
        exception
                when no_data_found then
                        cd_status_w := null;
        end;

        if (coalesce(cd_status_w::text, '') = '') then
                insert into DIAGNOSTICO_STATUS(
                        ie_status,
                        ds_status,
                        ie_situacao,
                        dt_atualizacao,
                        nm_usuario
                ) values ('P',
                        'Definitive',
                        'A',
                        clock_timestamp(),
                        nm_usuario_p
                );
                commit;
                cd_status_w := 'P';
        end if;


        if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_tipo_diagnostico_p IS NOT NULL AND ie_tipo_diagnostico_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_interno_p IS NOT NULL AND nr_seq_interno_p::text <> '')
        
        then

        insert into diagnostico_doenca_hist(nr_sequencia,
        dt_atualizacao,
        nm_usuario,
        ie_status,
        ds_observacao,
        cd_pessoa_fisica,
        cd_responsavel,
        cd_doenca,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        dt_diagnostico,
        nr_atendimento,
        ie_status_problema,
        dt_disease_end,
        nr_seq_interno,
        ie_tipo_diagnostico,
        ie_classificacao_doenca)

        SELECT nextval('diagnostico_doenca_hist_seq'),
        clock_timestamp(),
        nm_usuario_p,
        cd_status_w,
        ds_observacao_p,
        b.cd_pessoa_fisica,
        u.cd_pessoa_fisica,
        a.cd_doenca,
        clock_timestamp(),
        nm_usuario_p,
        a.dt_diagnostico,
        a.nr_atendimento,
        a.ie_status_problema,
        a.dt_fim,
        a.nr_seq_interno,
        a.ie_tipo_diagnostico,
        a.ie_classificacao_doenca
        from diagnostico_doenca   a,
        atendimento_paciente b,
        usuario              u
        where a.nr_seq_interno = nr_seq_interno_p
        and a.nr_atendimento = nr_atendimento_p
        and a.nr_atendimento = b.nr_atendimento
        and u.nm_usuario = nm_usuario_p;

        commit;

        end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
