SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pep_liberar_pac_isolamento_js ( nr_sequencia_p bigint, nr_atendimento_p bigint, nr_seq_justificativa_p bigint, dt_liberacao_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ds_observacao_p text) AS $body$
DECLARE


ie_lib_pac_isol_precaucao_w	varchar(1)	:= 'N';
qt_atendimento_precaucao_p	bigint	:= 0;

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin
	
	if (coalesce(nr_seq_justificativa_p::text, '') = '') then
		begin
		
		update	atendimento_precaucao
		set	dt_final_precaucao = clock_timestamp(),
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			nm_usuario_lib_iso = nm_usuario_p,
			ds_obs_lib_isolamento = ds_observacao_p
		where  nr_sequencia = nr_sequencia_p;
		
		end;
		
	elsif (nr_seq_justificativa_p IS NOT NULL AND nr_seq_justificativa_p::text <> '') then
		begin
		
		update	atendimento_precaucao
		set	dt_final_precaucao = dt_liberacao_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			nm_usuario_lib_iso = nm_usuario_p,
			nr_seq_movto_termino = nr_seq_justificativa_p ,
			ds_obs_lib_isolamento = ds_observacao_p
		where	nr_sequencia = nr_sequencia_p;
		
		end;
	end if;
	
	ie_lib_pac_isol_precaucao_w := obter_param_usuario(281, 874, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_lib_pac_isol_precaucao_w);
	
	select  count(*)
	into STRICT	qt_atendimento_precaucao_p
	from	atendimento_precaucao a,
		cih_precaucao b
	where	a.nr_seq_precaucao = b.nr_sequencia
	and	a.nr_atendimento = nr_atendimento_p
	and	coalesce(a.dt_final_precaucao::text, '') = ''
	and	coalesce(b.ie_isolamento,'N') = 'S'
	and	a.ie_situacao = 'A';
	
	if (ie_lib_pac_isol_precaucao_w = 'S') or (qt_atendimento_precaucao_p = 0) then
		begin
		
		update	atendimento_paciente
		set	ie_paciente_isolado = 'N',
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where  nr_atendimento = nr_atendimento_p;
		
		end;
	end if;
	
	CALL gerar_atendimento_precaucao(0,nr_atendimento_p,'T',0,nm_usuario_p,nr_sequencia_p);
	
	CALL gerar_motivo_isolamento(nr_atendimento_p,0,nm_usuario_p,'T',null,null,null,null,clock_timestamp(),nr_sequencia_p,nr_sequencia_p);
	
	CALL gerar_evento_regra(nr_atendimento_p,'LISO',nm_usuario_p);
	
	end;
	
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
