CREATE OR REPLACE PROCEDURE pep_med_fatur_proc_selecao ( ds_lista_proc_p text, nr_seq_fatur_p bigint, nm_usuario_p text, ie_via_acesso_p text) AS $body$
DECLARE

cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
ds_lista_proc_w		varchar(2000) := '';
ds_lista_proc_aux_w	varchar(2000) := '';
ds_lista_origem_w	varchar(2000) := '';
k			integer;
y			integer;


BEGIN



ds_lista_proc_w := ds_lista_proc_p;

while (ds_lista_proc_w IS NOT NULL AND ds_lista_proc_w::text <> '') loop
	begin

	select	position('_' in ds_lista_proc_w)
	into STRICT	k
	;

	if (k > 1) and ((substr(ds_lista_proc_w, 1, k -1) IS NOT NULL AND (substr(ds_lista_proc_w, 1, k -1))::text <> '')) then
		ds_lista_proc_aux_w		:= substr(ds_lista_proc_w, 1, k-1);
		cd_procedimento_w		:= replace(ds_lista_proc_aux_w, '_','');
		ds_lista_proc_w			:= substr(ds_lista_proc_w, k + 1, 2000);
	elsif (ds_lista_proc_w IS NOT NULL AND ds_lista_proc_w::text <> '') then
		cd_procedimento_w			:= replace(ds_lista_proc_w,'_','');
		ds_lista_proc_w			:= '';
	end if;

	select	position(',' in ds_lista_proc_w)
	into STRICT	y
	;

	if (y > 1) and ((substr(ds_lista_proc_w, 1, k -1) IS NOT NULL AND (substr(ds_lista_proc_w, 1, k -1))::text <> '')) then
		ds_lista_proc_aux_w		:= substr(ds_lista_proc_w, 1, y-3);
		ie_origem_proced_w		:= replace(ds_lista_proc_aux_w, ',','');
		ds_lista_proc_w			:= substr(ds_lista_proc_w, y + 1, 2000);
	elsif (ds_lista_proc_w IS NOT NULL AND ds_lista_proc_w::text <> '') then
		ie_origem_proced_w			:= replace(ds_lista_proc_w,',','');
		ds_lista_proc_w			:= '';
	end if;

	CALL inserir_pep_med_fatur_proc(nr_seq_fatur_p, cd_procedimento_w, ie_origem_proced_w, ie_via_acesso_p, 1, nm_usuario_p);

	end;
end loop;

commit;

end;
