SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pep_ver_imagens_js ( nm_usuario_tasy_p text, nr_prescricao_p bigint, nr_sequencia_p bigint, ds_caminho_result_p INOUT text, nm_usuario_integr_p INOUT text, ds_senha_usuario_integr_p INOUT text, ds_servico_p INOUT text, cd_tipo_procedimento_p INOUT bigint, nm_usuario_pixeon_p INOUT text, ds_senha_pixeon_p INOUT text) AS $body$
DECLARE


ds_caminho_result_w		varchar(255);
nm_usuario_integr_w		varchar(30);
ds_senha_usuario_integr_w		varchar(30);
ds_servico_w			varchar(80);
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_tipo_procedimento_w		smallint;

nm_usuario_pixeon_w		varchar(15);
ds_senha_pixeon_w		varchar(80);



BEGIN

select	max(ds_caminho_result)
into STRICT	ds_caminho_result_w
from	empresa_integr_dados
where	nr_seq_empresa_integr = 4;

if (nm_usuario_tasy_p IS NOT NULL AND nm_usuario_tasy_p::text <> '') then
	begin
	select	max(nm_usuario_integr),
		max(ds_senha_usuario_integr),
		max(ds_servico)
	into STRICT	nm_usuario_integr_w,
		ds_senha_usuario_integr_w,
		ds_servico_w
	from	lab_regra_usuario_integr
	where	nm_instituto = 'isite'
	and	nm_usuario_tasy = nm_usuario_tasy_p;
	end;
end if;

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select	cd_procedimento,
		ie_origem_proced
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_sequencia_p;


	select	cd_tipo_procedimento
	into STRICT	cd_tipo_procedimento_w
	from	procedimento
	where	cd_procedimento = cd_procedimento_w
	and	ie_origem_proced = ie_origem_proced_w;
	end;


end if;

select  max(nm_usuario_pixeon),
	max(ds_senha_pixeon)
into STRICT	nm_usuario_pixeon_w,
	ds_senha_pixeon_w
from    parametro_integracao_pacs
where   cd_estabelecimento  = wheb_usuario_pck.get_cd_estabelecimento;

ds_caminho_result_p	:= ds_caminho_result_w;
nm_usuario_integr_p	:= nm_usuario_integr_w;
ds_senha_usuario_integr_p	:= ds_senha_usuario_integr_w;
ds_servico_p		:= ds_servico_w;
cd_tipo_procedimento_p	:= cd_tipo_procedimento_w;

nm_usuario_pixeon_p := nm_usuario_pixeon_w;
ds_senha_pixeon_p := ds_senha_pixeon_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
