CREATE OR REPLACE PROCEDURE perform_authorization_service (CD_CONVENIO_P bigint, CD_CATEGORIA_P bigint, CD_CONTRACT_P text, CD_INDENTIFICATION_P bigint, CD_MEMBER_P bigint, CD_FAMILY_ID_P bigint, CD_PESSOA_FISICA_P bigint, CD_WEBID_P bigint, CD_BENEF_ID_P bigint, CD_SERVICE_P bigint, CD_DIAGNOSTICO_P text, CD_PROFESSIONAL_PROVIDER_P bigint, IE_SEND_TYPE_P text, NM_USUARIO_P text, NR_SEQ_TRANS_P INOUT bigint) AS $body$
DECLARE


DT_BIRTH_W		W_AUT_SERV_TRANSACTION.DT_BIRTH%type;
CD_PRODUCT_W	CATEGORIA_CONVENIO.CD_INTEGRACAO%type;
	

BEGIN

if (IE_SEND_TYPE_P = 'VC') then
	select 	nextval('w_aut_serv_transaction_seq')
	into STRICT 	NR_SEQ_TRANS_P
	;
	
	begin
	select 	dt_nascimento
	into STRICT 	dt_birth_w
	from	pessoa_fisica
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p;
	
	exception
	when others then
		dt_birth_w := null;
	end;
	
	begin
	select 	cd_integracao
	into STRICT	cd_product_w
	from   	categoria_convenio
	where 	cd_convenio = cd_convenio_p  
	and 	cd_categoria = cd_categoria_p;
	
	exception
	when others then
		cd_product_w := null;
	end;
	
	insert into W_AUT_SERV_TRANSACTION(
					NR_SEQUENCIA,
					CD_CONTRACT,
					CD_IDENTIFICATION,
					CD_MEMBER,
					CD_SERVICE,
					IE_SEND_TYPE,
					CD_FAMILY_ID,
					DT_BIRTH,
					CD_PRODUCT,
					NR_FIRST_PAGINATION,
					NR_LAST_PAGINATION,
					NM_USUARIO,
					DT_ATUALIZACAO,
					NM_USUARIO_NREC,
					DT_ATUALIZACAO_NREC,
					CD_CONVENIO)
					values (
					NR_SEQ_TRANS_P,
					CD_CONTRACT_P,
					CD_INDENTIFICATION_P,
					CD_MEMBER_P,
					CD_SERVICE_P,
					IE_SEND_TYPE_P,
					coalesce(CD_FAMILY_ID_P,0),
					DT_BIRTH_W,
					CD_PRODUCT_W,
					1,
					20,
					NM_USUARIO_P,
					clock_timestamp(),
					NM_USUARIO_P,
					clock_timestamp(),
					CD_CONVENIO_P);
elsif (IE_SEND_TYPE_P = 'VB') then
	select 	nextval('w_aut_serv_transaction_seq')
	into STRICT 	NR_SEQ_TRANS_P
	;
	
	insert into W_AUT_SERV_TRANSACTION(
					NR_SEQUENCIA,
					CD_WEBID,
					CD_BENEF_ID,
					CD_SERVICE,
					CD_PROFESSIONAL_PROVIDER,
					CD_CONTRACT,
					CD_IDENTIFICATION,
					CD_MEMBER,
					CD_FAMILY_ID,
					CD_EMERGENCY,
					CD_SPECIALTY,
					CD_REFER,
					IE_SEND_TYPE,
					NM_USUARIO,
					DT_ATUALIZACAO,
					NM_USUARIO_NREC,
					DT_ATUALIZACAO_NREC,
					CD_CONVENIO,
					CD_DIAGNOSTICO)
					values (
					NR_SEQ_TRANS_P,
					CD_WEBID_P,
					CD_BENEF_ID_P,
					CD_SERVICE_P,
					lpad(CD_PROFESSIONAL_PROVIDER_P,5,0),
					CD_CONTRACT_P,
					CD_INDENTIFICATION_P,
					CD_MEMBER_P,
					CD_FAMILY_ID_P,
					0,
					0,
					0,
					IE_SEND_TYPE_P,
					NM_USUARIO_P,
					clock_timestamp(),
					NM_USUARIO_P,
					clock_timestamp(),
					CD_CONVENIO_P,
					CD_DIAGNOSTICO_P);
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
