CREATE OR REPLACE PROCEDURE perform_record_signature ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nm_usuario_p usuario.nm_usuario%type, nr_proj_assinatura_p tasy_projeto_assinatura.nr_sequencia%type, nm_tabela_p tabela_sistema.nm_tabela%type, nr_seq_registro_p bigint, nr_seq_visao_p tabela_visao.nr_sequencia%type, cd_funcao_p funcao.cd_funcao%type, ie_tipo_assinatura_p tasy_assinatura_lote.ie_tipo_assinatura%type ) AS $body$
DECLARE


	_ora2pg_r RECORD;
qt_reg_w			integer;
	nm_usuario_w		usuario.nm_usuario%type not null := nm_usuario_p;
	nr_seq_projeto_w	tasy_projeto_assinatura.nr_sequencia%type;
	cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
	cd_pessoa_atendimento_w	pessoa_fisica.cd_pessoa_fisica%type;
	nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
	ds_erro_w			varchar(2000);
	ie_print_batch_w varchar(1);


BEGIN
	cd_pessoa_fisica_w := cd_pessoa_fisica_p;
	nr_seq_projeto_w := nr_proj_assinatura_p;
	nr_atendimento_w := nr_atendimento_p;

	select count(a.nr_sequencia)
	  into STRICT qt_reg_w
	  from tasy_assinatura_lote a
	 where a.nr_proj_assinatura = nr_seq_projeto_w
	   and a.nm_usuario = nm_usuario_w
	   and a.nr_seq_registro = nr_seq_registro_p;

	select coalesce(max(ie_permite_lote),'N')
		into STRICT    ie_print_batch_w
		from    tasy_proj_assin_perfil
		where   nr_seq_proj = nr_seq_projeto_w
		and     coalesce(cd_perfil, obter_perfil_ativo) = obter_perfil_ativo;

	if (qt_reg_w = 0 and (ie_print_batch_w IS NOT NULL AND ie_print_batch_w::text <> '') and ie_print_batch_w <> 'N') then

		cd_pessoa_atendimento_w := obter_pessoa_atendimento(nr_atendimento_w, 'C');
		if ((cd_pessoa_atendimento_w IS NOT NULL AND cd_pessoa_atendimento_w::text <> '') and (coalesce(cd_pessoa_atendimento_w, 'XPTO') <> coalesce(cd_pessoa_fisica_w, 'XPTO'))) then
			cd_pessoa_fisica_w := cd_pessoa_atendimento_w;
		end if;
		
		if ((coalesce(cd_pessoa_fisica_w::text, '') = '') and (nm_tabela_p = 'PARECER_MEDICO') and (nr_seq_registro_p IS NOT NULL AND nr_seq_registro_p::text <> '')) then
			begin
				select	a.NR_ATENDIMENTO, a.CD_PESSOA_FISICA
				into STRICT	nr_atendimento_w, cd_pessoa_fisica_w
				from	PARECER_MEDICO_REQ a, PARECER_MEDICO b
				where 	a.nr_parecer = b.nr_parecer
				and	b.nr_seq_interno = nr_seq_registro_p;
			end;
		end if;
		
		if ((coalesce(nr_atendimento_w, 0) = 0 and coalesce(cd_pessoa_fisica_w, 'XPTO') = 'XPTO') OR nm_tabela_p = 'CIRURGIA_PARTICIPANTE')  then
			SELECT * FROM Obter_pf_atend_cirurg_dinamico(nm_tabela_p, nr_seq_registro_p) INTO STRICT _ora2pg_r;
 cd_pessoa_fisica_w := _ora2pg_r.cd_pessoa_fisica_p; nr_atendimento_w := _ora2pg_r.nr_atendimento_p;
		end if;
		
		insert into tasy_assinatura_lote(
			nr_sequencia,
			nr_atendimento,
			cd_pessoa_fisica,
			nm_usuario,
			nr_proj_assinatura,
			ie_tipo_assinatura,
			nm_tabela,
			nr_seq_registro,
			nr_seq_visao,
			cd_funcao,
			ie_item_pendencia,
			dt_registro
		) values (
			nextval('tasy_assinatura_lote_seq'),
			nr_atendimento_w,
			cd_pessoa_fisica_w,
			nm_usuario_w,
			nr_seq_projeto_w,
			ie_tipo_assinatura_p,
			nm_tabela_p,
			nr_seq_registro_p,
			nr_seq_visao_p,
			coalesce(cd_funcao_p, obter_funcao_ativa),
			obtain_type_signature_record(nr_seq_projeto_w),
			clock_timestamp()
		);

		commit;
	end if;

exception when others then
	ds_erro_w := substr(sqlerrm,1,2000);

	rollback;
	CALL gravar_log_tasy(10010, ds_erro_w, nm_usuario_w);
	commit;

	CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_erro_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
